#include <iostream>
#include <cmath>

int main()
{
    using namespace std;
    double x, y, z, Vmag;
    cout << "Enter x, y, and z coordinates of the vector: ";
    cin >> x >> y >> z;
    Vmag = sqrt(x*x + y*y + z*z);
    x /= Vmag;
    y /= Vmag;
    z /= Vmag;
    cout << "The normalized vector is: (" << x << "," << y << "," << z << ")" << endl;
